package uebung_2_2;

public class BinomischeFormel {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			double a = 8;
			double b = 3; 
			
			double loesung1 = (a*a)+(2*a*b)+(b*b);
			double loesung2 = (a+b)*(a+b);
					
		System.out.println("Lösung 1 =" + loesung1);
		System.out.println("Lösung 2 =" + loesung2);

	}

}
